func FloorDirection(inst string) int {
	floor := 0
	up := '('
	down := ')'

	for _, c := range inst {
		if c == up {
			floor++
		} else if c == down {
			floor--
		}
	}
	return floor
}

func FloorPosition(inst string) int {
	res := 0
	var basement int
	for i, ch := range inst {
		if ch == '(' {
			res += 1
		} else if ch == ')' {
			res -= 1
		}
		if res == -1 {
			basement = i + 1
			break
		}
	}
	return basement
}
